import axios from "axios"
import { defineStore } from "pinia"

const BASE_URL = 'http://localhost:5169/'

const useUser = defineStore("User", {
    state: () => ({
        isLoggedIn: false,
        user: ({
            userId: "",
            userName: "",
            userRole: "",
            jwtVersion: "",
            exp: 0
        })
    }),
    getters: {

    },
    actions: {
        async getCode(phone) {
            await axios.post(BASE_URL + 'api/Login/GetCode', {
                phone: {
                    regionNumber: 0,
                    phoneNumberValue: "string"
                },
                password: "string",
                code: "string"
            })
        }
    }
})

export default useUser;
