import axios from "axios";
import { defineStore } from "pinia";

const BASE_URL = "http://localhost:5172/"

const useOrder = defineStore("Order", {
    state: () => ({
        Order: [],
        OrderDetail: []
    }),
    getters: {},
    actions: {
        async getOrder() {
            axios.get(BASE_URL + 'api/Order/GetOrders').then(res => {
                console.log(res.data.data)
                this.Order = res.data.data
            })
        },
        async getOrderDetails(orderId) {
            axios.get(BASE_URL + 'api/Order/GetOrderDetail/' + orderId).then(res => {
                console.log(res.data.data)
                this.Order = res.data.data
            })
        }
    }
})

export default useOrder
