import "core-js/modules/es.array.push.js";
import useOrder from "@/stores/useOrder";
import useProductList from "@/stores/useProductList";
import { onMounted, reactive, ref } from "vue";
export default {
  __name: 'Order',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const ProductList = useProductList();
    const Order = useOrder();
    const OrderProduct = ref([]);
    onMounted(async () => {
      //组装Product

      const OrderObj = await Order.getOrder();
      console.log("OrderObj", OrderObj);
      for (let detail of OrderObj) {
        const OrderDetail = await Order.getOrderDetails(detail.id);
        console.log(OrderDetail);
        const titleNames = [];
        for (let productDetail of OrderDetail) {
          //向Product发送ProductId 请求商品
          const product = await ProductList.getProductById(productDetail.productId);
          titleNames.push(product.title);
          //console.log(product)
        }

        console.log(titleNames);
        const OrderProductDetail = reactive({
          orderId: detail.id,
          title: titleNames[0] + `等${OrderDetail.length}件商品`,
          dateTime: detail.orderDate,
          totalPrice: detail.totalPrice,
          orderDetails: []
        });
        OrderProduct.value.push(OrderProductDetail);
      }
    });
    const __returned__ = {
      ProductList,
      Order,
      OrderProduct,
      get useOrder() {
        return useOrder;
      },
      get useProductList() {
        return useProductList;
      },
      onMounted,
      reactive,
      ref
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
